  @GetMapping({"all"})
  public InterestEntityResbody getInterests(@RequestHeader Map<String, String> headers) {
    InterestEntityResbody allInterests = this.service.getInterests(headers.get("id"));
    return allInterests;
  }
  
  @PostMapping({"setUserInterests"})
  public String setInterests(@RequestBody SetInterestReqBody interestIds, @RequestHeader Map<String, String> headers) {
    this.service.saveUserInterests(headers.get("id"), interestIds);
    return "successs";
  }
  
